function removeBotoesAtivos(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("option--is-active")}function escondeTodasAsTabs(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("selector__selected_tab--is-active")}document.addEventListener("DOMContentLoaded",()=>{var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);escondeTodasAsTabs(),removeBotoesAtivos(),e.classList.add("selector__selected_tab--is-active"),t.target.classList.add("option--is-active")})});